import datetime
import decimal
import json


class CJsonEncoder(json.JSONEncoder):
    def default(self, obj):
        if isinstance(obj, datetime.datetime):
            return obj.strftime('%Y-%m-%d %H:%M:%S')
        elif isinstance(obj, datetime.date):
            return obj.strftime("%Y-%m-%d")
        elif isinstance(obj, datetime.time):
            return obj.strftime('%H:%M:%S')
        elif isinstance(obj, decimal.Decimal):
            return float(obj)
        # elif isinstance(obj, (np.ndarray, np.generic)):
        # 	  return obj.tolist()
        else:
            return json.JSONEncoder.default(self, obj)

from typing import Generic, TypeVar

from pydantic import BaseModel, ConfigDict, computed_field
from pydantic.types import NonNegativeInt, PositiveInt


class BaseSchema(BaseModel):
    model_config = ConfigDict(
        validate_assignment=True,
        extra="ignore",
    )

    def dict(self):
        """A shortcut to dump the model as a dict with model_dump()."""
        return self.model_dump()


class RequestBase(BaseSchema):
    """Base model for request params."""

    pass


class ResponseBase(BaseSchema):
    """Base model for response data."""

    code: int = 0
    msg: str = ""
    data: dict | BaseSchema | None


T = TypeVar("T")


class PageData(BaseSchema, Generic[T]):
    items: list[T]
    page: PositiveInt
    page_size: PositiveInt
    item_count: NonNegativeInt

    @computed_field
    @property
    def page_count(self) -> NonNegativeInt:
        return (self.item_count - 1) // self.page_size + 1


class PaginationMixin:
    page: PositiveInt = 1
    page_size: PositiveInt = 20

    def get_offset(self) -> NonNegativeInt:
        """Get the offset based on the page and page_size.

        Returns
            NonNegativeInt: The offset value.
        """
        return (self.page - 1) * self.page_size

import math
import bpy


def get_keys(obj_list):
    """get keyframes of object list"""
    keys = set()
    for obj in obj_list:
        anim = obj.animation_data
        if anim is not None and anim.action is not None:
            for fcu in anim.action.fcurves:
                for keyframe in fcu.keyframe_points:
                    x, y = keyframe.co
                    if x not in keys:
                        keys.add((math.ceil(x)))
    return list(keys)


def get_key_scope(obj_list):
    min_key = None
    max_key = None

    for obj in obj_list:
        if obj.animation_data:
            # loop through all fcurves
            for fcurve in obj.animation_data.action.fcurves:
                # loop through all keyframe points
                for point in fcurve.keyframe_points:
                    # get frame number of point
                    frame = point.co.x
                    # update min key and max key variables
                    if min_key is None or frame < min_key:
                        min_key = frame
                    if max_key is None or frame > max_key:
                        max_key = frame
    return min_key, max_key

