def rate_limiter(interval: int | float):
    """Rate limiter decorator that enforces a minimum time between function calls.

    Args:
        interval: The minimum number of seconds between function calls.
    """
    last_call_time: list[float] = [0]  # Using list to make it mutable in the inner function

    def decorator(func: Callable) -> Callable:
        @wraps(func)
        def wrapper(*args, **kwargs):
            current_time = time.time()
            time_since_last_call = current_time - last_call_time[0]
            if time_since_last_call < interval:
                sleep_time = interval - time_since_last_call
                # logger.debug(f"Rate limit reached. Sleeping for {sleep_time:.2f} seconds")
                time.sleep(sleep_time)

            last_call_time[0] = time.time()
            return func(*args, **kwargs)

        return wrapper

    return decorator

from pathlib import Path
from pydantic import AfterValidator


def _is_file(v: Path) -> Path:
    if not v.is_file():
        raise ValueError(f"File {v} does not exist.")
    return v


def _is_dir(v: Path) -> Path:
    if not v.is_dir():
        raise ValueError(f"Directory {v} does not exist.")
    return v


